import React from 'react';
import ImageInfoBlock from './ImageInfoBlock';

const dataListBlock = {

}

function DataListBlock(props) {

    var dataArr = [];
    Object.keys(props.jsonData).forEach(key => dataArr.push(props.jsonData[key]));


    return (
        <div style={ dataListBlock } className="dataListBlock">
            <h1>MORE INFO</h1>

            {
                //Output a Modal component for each object in dataArr
                dataArr.map((item, i) => {
                    var dataList = '';

                    dataList += i < dataArr.length-1 
                        ? '<ul>'
                        : '<ul style="list-style-type: none">';

                    for(var j = 0; j < item.list.length; j++){
                        dataList += '<li>' + item.list[j] + '</li>';
                    }

                    dataList += '</ul>';

                    return i < dataArr.length-1 ? 
                        <ImageInfoBlock 
                            key={item.id}
                            isDark={ i%2===0 } 
                            flip={ i%2===0  }
                            image={item.image} 
                            headerContent={item.heading} 
                            textContent={ 
                                <div dangerouslySetInnerHTML={{__html: dataList}}></div>
                            }
                        />
                     : 
                     <div key={item.id} style={{padding: '4vw'}}>
                         <h1>{item.heading}</h1>
                         <div dangerouslySetInnerHTML={{__html: dataList}} style={{fontWeight: '600', lineHeight: '30px'}}></div>
                     </div>;
                })
            }

        </div>
    );
}

export default DataListBlock;
