import React from 'react';

function Modal(props) {

    const modalContainer = {
        maxHeight: '400px',
        madWidth: '300px',
        minHeight: '200px',
        minWidth: '150px',
        backgroundColor: 'rgba(26,56,111,.65)',
        color: '#fff',
        margin: '0 20px 0 20px',
        borderRadius: '3%',
        display: 'flex',
        flexDirection: 'column',

    }

    const imageContainer = {
        display: 'inline-block',
        width: '100%',
        height: '60%',
        background: `url(${props.image})`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
    }

    const contentContainer = {
        margin: '0',
        backgroundColor: 'rgba(4,4,4,.5)',
        height:'95%',
        width: '100%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-around',
        alignItems: 'center',
    }

    const heading = {
        margin: '2%',
        fontSize: '100%',
    }

    const text = {
        fontSize: '80%',
        padding: '1vmin',
    }

    const button = {
        border: 'none',
        height: '100%',
    }

  return (
    <>    
        <div style={ modalContainer }>
            <div style={ imageContainer }>
                {/* <img src={props.image} alt={ props.heading + ' Modal' } width="100%" style={{borderRadius: '3%'}}/> */}
            </div>
            <div style={ contentContainer }>
                <h2  style={ heading }>
                    {props.heading}
                </h2>
                <div  style={ text }>
                    {props.text}
                </div>
                <div>
                    <button style={ button }>{props.buttonTxt || "Button"}</button>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
