import React from 'react';
import { useForm } from 'react-hook-form';

const emailForm = {
    background: 'red',
}

const heading = {
    fontSize: '3vw',
    fontWeight: '400',
    margin: '2vw 0',
}

const formCont = {

}

function EmailForm(props) {
    const {register, handleSubmit, errors} = useForm();

    const onSubmit = (data) => {
        console.log(data);
    }

    return (
        <div style={ emailForm } className="emailForm">
            <div style={ heading }>{ props.heading }</div>
            {errors.email && <p>Invalid email</p>}
            <div style={ formCont }>

                <form onSubmit={handleSubmit(onSubmit)}>
                    {/* Name Input*/}
                    <input 
                        type="text" 
                        placeholder="Name" 
                        name="name" 
                        ref={register} 
                    />
                    {/* Email Input*/}
                    <input 
                        type="text" 
                        placeholder="Email" 
                        name="email" 
                        ref={register({ pattern: /^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/i })} 
                    />
                    {/* Subject Input*/}
                    <input 
                        type="text" 
                        placeholder="Subject" 
                        name="subject" 
                        ref={register} 
                    />
                    {/* Message Input*/}
                    <input 
                        type="text" 
                        placeholder="Message" 
                        name="Message" 
                        ref={register} 
                    />
                    {/* Submit Button */}
                    <input type="submit" />
                </form>

            </div>
        </div>
    );
}

export default EmailForm;
