import React from 'react';

function Modal(props) {

    const modalContainer = {
        minHeight: '300px',
        minWidth: '250px',
        height: '40vw',
        maxHeight: '450px',
        backgroundColor: 'rgba(0,0,0, 0.4)',
        color: '#fff',
        margin: '0 20px 0 20px',
        borderRadius: '3%',
        display: 'flex',
        flexDirection: 'column',
        border: '2px solid white',
    }

    const imageContainer = {
        width: '100%',
        height: '40%',
        background: `url(${props.image})`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        borderRadius: 'inherit',
    }

    const contentContainer = {
        // backgroundColor: 'rgba(4,4,4,.4)',
        height: '60%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
    }

    const heading = {
        // backgroundColor: 'red',
        fontSize: '200%',
        margin: 0,
    }

    const text = {
        // backgroundColor: 'green',
        flex: '2',
        fontSize: '80%',
        padding: '8px',
    }

    const buttonCont = {
        // backgroundColor: 'cyan',
        padding: '10px',
        flex: '1',
    }

    const button = {
        backgroundColor: 'rgba(4, 29, 46, .8)',
        color: 'white',
        fontSize: '1em',
        fontWeight: '600',
        border: 'none',
        height: '50%',
        minHeight: '25px',
        padding: '0 20%',
        borderRadius: '5px',
    }

  return (
    <>    
        <div style={ modalContainer } className="modalContF">
            <div style={ imageContainer }></div>
            <div style={ contentContainer }>
                <h1  style={ heading }>
                    {props.heading}
                </h1>
                <div  style={ text }>
                    {props.text}
                </div>
                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
