import React, {useState} from 'react';
import ImageInfoBlock from './ImageInfoBlock';

const dataListBlock = {

}

function DataListBlock(props) {
    const [isDark, setIsDark] = useState(false);

    var dataArr = [];

    //  Grabs data from the props.jsonData file and pushes each modal object into the dataArr array.
    /*  
        I feel like this needs an explanation:
            This was done so that later on, I can set up a CMS for CBSI to update their site more easily. Eventually, Ill populate
            that props.jsonData file using CMS options for any user grated such privelages. For now, it doesnt cause  any
            performance issues, and will stay this way until the CMS portion is completed.

            The modals are currently entirely created and populated using data from the props.jsonData file. 
    */
    Object.keys(props.jsonData).forEach(key => dataArr.push(props.jsonData[key]));


    return (
        <div style={ dataListBlock } className="dataListBlock">
            <h1>MORE INFO</h1>

            {
                //Output a Modal component for each object in dataArr
                dataArr.map(item => 
                        <ImageInfoBlock 
                            isDark={ false } 
                            flip={ true }
                            image={item.image} 
                            headerContent="OUR MISSION" 
                            textContent="
                                Our safety-oriented focus on customer service and quality craftsmanship has been 
                                valued by our customers. We’ve had the unique privilege of working on some of the 
                                most prestigious commercial, multi-family and historic government properties in the 
                                Washington, D.C. metropolitan area.  Click on the “View Projects” button below to see 
                                a comprehensive list of our projects.
                            " 
                        />
                )
            }
        </div>
    );
}

export default DataListBlock;
