import React from 'react';

var imageInfoBlock = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    padding: '2vw 0',
}

const heading = {
    fontSize: '6vw',
}

const imgTxt = {

}

var img = {
    // height: '27vw',
    width: '100%',
    borderRadius: '3px',
}

var textBl = {
    flex: 2,
    display: 'flex',
    flexDirection: 'column',
    padding: '0 3vw 3vw 3vw 3vw',
}

var textContBlock = {
    width: '90%',
    fontSize: '4vw',
    fontWeight: '300',
    display: 'flex',
    margin: '0 0 0 2.3vw',
}

function ImageInfoBlockSm(props) {

    var color = props.props.isDark 
        ? { background: 'rgba(63, 73, 83, 1)', color: 'white' } 
        : { background: '', color: 'rgba(44,42,41)' };

    return (
        <div style={ {...imageInfoBlock, ...color} } className="imageInfoBlock" id={props.id}>
            <div style={ heading }>{props.props.headerContent}</div>
            <div style={ imgTxt }>
                <img src={ props.props.image || './images/placeholder.jpg' } alt={props.headding} style={ img } />
                <div style={ textBl }>
                    <div style={textContBlock}>
                        {props.props.textContent}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ImageInfoBlockSm;
