import React from 'react';

function ContactCard(props) {

    const contactCard = {
        // background: 'red',
        display: 'flex',
        // justifyContent: 'space-around',
        width: '100%',
        height: '100%',
    }

    const ImgCont = {
        background: `url(${props.image})`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        display: 'block',
        width: '15vw',
        height: '100%',
        maxWidth: '200px',
        borderRadius: '50%',
        border: '1px solid rgb(166, 25, 46)',
    }

    const contDesc = {
        // background: 'green',
        height: '100%',
        width: '60%',
        display: 'flex',
        flexDirection: 'column',
    }

    const nameDescBl = {
        // background: 'cyan',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'start',
    }

    const contactDesc = {
        // background: 'orange',
        fontSize: '2.5vw',
        fontWeight: '600',
        letterSpacing: '1px',
        margin: '2vw 0 5vw 0',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'start',
        margin: '1vw 0 0 1vw',
    }


    return ( 
        <>
            <div style={ contactCard }>
                <div style={ ImgCont }></div>
                <div style={ contDesc }>
                    <div style={ nameDescBl }>
                        <div style={{fontWeight: '600',fontSize: '2vw'}}> {props.name} </div>
                        <div style={{fontSize: '1.3vw'}}> {props.title} </div>
                    </div>
                    <div style={ contactDesc }>
                        {props.email}
                    </div>
                </div>
            </div>
        </>
    );
}

export default ContactCard;
