import React from 'react';
import { Link } from 'react-router-dom';
import glamorous from 'glamorous';

var prevScrollPos = window.pageYOffset;

export class Nav extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            navStyle: 'linear-gradient(180deg, rgba(0,0,0,0.5965346534653465) 37%, rgba(95,113,140,0) 99%)'
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
        document.getElementById('navCont').style.background = 'linear-gradient(180deg, rgba(0,0,0,0.5965346534653465) 37%, rgba(95,113,140,0) 99%)';
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.handleScroll);
    }

    handleScroll() {
        var currentScrollPos = window.pageYOffset;
        var targetEle = document.getElementById('navCont');
        targetEle.style.top = prevScrollPos > currentScrollPos 
            ? '0'
            : '-10vw';

        targetEle.style.background = currentScrollPos > 100 
            ? 'linear-gradient(180deg, rgba(0,0,0,1) 11%, rgba(33,33,33,1) 81%)'
            : 'linear-gradient(180deg, rgba(0,0,0,0.5965346534653465) 37%, rgba(95,113,140,0) 99%)';

        prevScrollPos = currentScrollPos;
    }

    render() {
    const navCont = {
        position: 'fixed',
        top: 0,
        width: '100%',
        transition: 'top 0.3s',
    }

    const subNav = {
        display: 'flex',
        justifyContent: 'flex-end',
        alignItems: 'center',
        height: '15px',
        background: 'rgb(55,54,54)',
        color: 'white',
    }

    const subNavLink = {
        color: '#fff',
        fontSize: '80%',
        fontWeight: 100,
        width: '130px',
        margin: '0',
    }

    const nav = {
        display: 'flex',
        justifyContent: 'space-around',
        alignItems: 'center',
        height: '6vw',
        maxHeight: '50px',
        minHeight: '40px',    
        padding: '2px',

    }

    const navLogo = {
        height: '4.9vw',
        minHeight: '40px',
        maxHeight: '45px',
        margin: '0 0 2px 0',
    }

    const navLinks = {
        width: '100%',
        display: 'flex',
        justifyContent: 'flex-end',
        alignItems: 'center',
        listStyle: 'none',
        padding: '0 6vw 0 0',
    }

    const LinkText = glamorous.li({
        color: '#fff',
        fontWeight: 500,
        fontSize: '1.2vw',
        letterSpacing: '2px',
        textDecoration: 'none',
        padding: '10px 2vw',
        margin: '0 1vw 0 0',
        borderRadius: '30px',
        ':hover' : {
            color: '#FFF',
            backgroundColor: 'rgba(200,200,200, .2)',
        }
    })

    const links = { textDecoration: 'none' }

    return (
        <div style={navCont} id="navCont">

            <div style={ subNav }>
                <a href="http://www.cbsi.co/ams" style={ subNavLink }>Employee Portal</a>
                <Link style={ subNavLink } to="/contact">Contact Us</Link>
            </div>

            <nav style={ nav }>
            <Link style={{maxHeight: 'inherit'}} to='/'>
                <img src="/images/navLogo4.png" alt="CBSI" style={ navLogo }/>
            </Link>
                <ul style={ navLinks }>
                    <Link style={links} to='/'>
                        <LinkText>HOME</LinkText>
                    </Link>
                    <Link style={links} to='/about'>
                        <LinkText>ABOUT</LinkText>
                    </Link>
                    <Link style={links} to='/services'>
                        <LinkText>SERVICES</LinkText>
                    </Link>
                    <Link style={links} to='/projects'>
                        <LinkText>OUR PROJECTS</LinkText>
                    </Link>
                    <Link style={links} to='/contact'>
                        <LinkText>CONTACT</LinkText>
                    </Link>
                </ul>
            </nav>

        </div>
    );
    }
}

export default Nav;
