import React from 'react';
import Modal from './Modal';
import data from './data/dataFeed.json';

export class ScrollableArea extends React.Component {

    render() {

        const scrollableAreaCont = {
            display: 'block',
            overflowX: 'scroll',
            overflowY: 'none',
            minHeight: '250px',
            overflow: 'auto',
            padding: '15px 0 15px 0',

        }

        const scrollableAreaContFlex = {
            display: 'flex',
            justifyContent: 'space-between',
            height: 'inherit',
        }

        var dataArr = [];

        Object.keys(data).forEach(key => dataArr.push(data[key]));

        return (
            <>
                <h1 className="scAreaHeading">{this.props.heading}</h1>
                <div className="scrollableAreaCont">
                    <div className="scrollableAreaContFlex">
                        {
                            dataArr.map(item => 
                                <Modal 
                                    key={ item.id }
                                    image={ item.image } 
                                    heading={ item.heading } 
                                    text={ item.text } 
                                    buttonTxt={ item.buttonTxt } 
                                />
                            )
                        }
                    </div>
                </div>
            </>
        )
    }
}

export default ScrollableArea;
