import React from 'react';

function Modal(props) {

    const modalContainer = {
        height: '30vmin',
        width: '100vmin',
        backgroundColor: 'rgba(26,56,111,.65)',
        color: '#fff',
        margin: '20px',
        borderRadius: '3%',
        overflow: 'none',

    }

    const imageContainer = {
        width: '100%',
        overflow: 'none',
        position: 'relative',
        top: '0',
    }

    const contentContainer = {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '5px',
        overflow: 'none',
    }

    const heading = {
        margin: '2%',
        fontSize: '150%',
    }

    const text = {
        fontSize: '100%',
        padding: '.5vmin',
    }

    const button = {
        border: 'none',
        height: '100%',
    }

  return (
    <>    
        <div style={ modalContainer }>
            <div style={ imageContainer }>
                <img src={props.image} alt={ props.heading + ' Modal' } width="100%" style={{borderRadius: '3%'}}/>
            </div>
            <div style={ contentContainer }>
                <h2  style={ heading }>
                    {props.heading}
                </h2>
                <div  style={ text }>
                    {props.text}
                </div>
                <div>
                    <button style={ button }>{props.buttonTxt || "Button"}</button>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
