import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

var prevScrollPos = window.pageYOffset;

const navCont = {
    background: 'linear-gradient(180deg, rgba(0,0,0,0.5965346534653465) 37%, rgba(95,113,140,0) 99%)',
    position: 'fixed',
    top: 0,
    width: '100%',
    transition: 'top 0.3s',
    zIndex: '1000',
}

const subNav = {
    display: 'flex',
    justifyContent: 'flex-end',
    alignItems: 'center',
    height: '15px',
    background: 'rgb(55,54,54)',
    color: 'white',
    // display: 'none',
}

const subNavLink = {
    color: '#fff',
    fontSize: '1.5vw',
    fontWeight: 400,
    width: '15vw',
    margin: '0',
}

const nav = {
    display: 'flex',
    justifyContent: 'space-around',
    alignItems: 'center',
    height: '6vw',
    maxHeight: '50px',
    minHeight: '40px',  
    padding: '3px 0 0 0',
}

const navLogoCont = {
    maxHeight: 'inherit', 
    height: 'inherit', 
    display: 'flex', 
    alignItems: 'center'
}

const navLogo = {
    margin: '0 0 .3vw 6vw',
    height: '4.35vw',
    maxHeight: '40px',
    minHeight: '23px'
}

const navLinks = {
    width: '100%',
    display: 'flex',
    justifyContent: 'flex-end',
    alignItems: 'center',
    listStyle: 'none',
    padding: '0 6vw 0 0',
}

const linkText = {
    color: '#fff',
    fontWeight: 600,
    fontSize: '1.2vw',
    letterSpacing: '2px',
    textDecoration: 'none',
    padding: '5px 1vw',
    margin: '0 1vw 0 0',
    borderRadius: '30px',
}

const links = { textDecoration: 'none' }

// ------------
function Nav() {

    function handleScroll() {
        var currentScrollPos = window.pageYOffset;
        var targetEle = document.getElementById('navCont');
        targetEle.style.top = prevScrollPos > currentScrollPos
            ? '0'
            : '-10vw';

        targetEle.style.background = currentScrollPos > 50 
            ? 'linear-gradient(180deg, rgba(0,0,0,1) 11%, rgba(33,33,33,1) 81%)'
            : 'linear-gradient(180deg, rgba(0,0,0,0.5965346534653465) 37%, rgba(95,113,140,0) 99%)';

        prevScrollPos = currentScrollPos;
    }

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return() =>{
            window.removeEventListener('scroll', handleScroll);
        }
    }, []);


    return (
        <div style={navCont} id="navCont">

            <div style={ subNav }>
                <a href="http://www.cbsi.co/ams" style={ subNavLink }>Employee Portal</a>
                <Link style={ subNavLink } to="/contact">Contact Us</Link>
            </div>

            <nav style={ nav }>
            <Link style={ navLogoCont } to='/'>
                <img src="/images/navLogo.png" alt="CBSI" style={ navLogo }/>
            </Link>
                <ul style={ navLinks }>
                    <Link style={links} to='/'>
                        <div style={linkText} >HOME</div>
                    </Link>
                    <Link style={links} to='/about'>
                        <div style={linkText} >ABOUT</div>
                    </Link>
                    <Link style={links} to='/services'>
                        <div style={linkText} >SERVICES</div>
                    </Link>
                    <Link style={links} to='/projects'>
                        <div style={linkText} >OUR PROJECTS</div>
                    </Link>
                    <Link style={links} to='/contact'>
                        <div style={linkText} >CONTACT</div>
                    </Link>
                </ul>
            </nav>

        </div>
    );
}

export default Nav;
