import React, { useEffect, useState } from 'react';

// Mobile Styles
var slider = {
    background: 'rgba(44,42,41, .9)',
    color: 'white',
    height: '200px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    borderRadius: '5% 5% 0 0',
    padding: '5px 3px 0 3px',
    position: 'relative',
    top: '35px',
}

// start at top: '210px',
// get to top: '35px',

const headingBl = {
    fontSize: '18px',
}

const textBl = {
    fontSize: '.8em',
    fontWeight: '300',
    letterSpacing: '2px',
}

const buttonCont = {
    padding: '0 0 5px 0',
}

const button = {
    background: 'rgb(166, 25, 46)',
    color: 'white',
    fontSize: '.9em',
    fontWeight: '600',
    height: '25px',
    width: '95%',
    padding: '0 5px',
    border: 'none',
    borderRadius: '5px',
}

function ImageCard(props) {
    var [slideUp, setSlideUp] = useState(false);

    useEffect(() => {
        var sliderCont = document.getElementById(`${props.keyx}`);

        function handleHover() {
            setSlideUp(true)
            console.log(slideUp)
        }

        sliderCont.addEventListener('mouseenter', handleHover)
    }, []);

    var imageCard = {
        background: `url('${props.image || './images/placeholder.jpg'}')`,
        width: '180px',
        height: '240px',
        borderRadius: '3%',
        overflow: 'hidden',
    }

    var slider = {
        background: 'rgba(44,42,41, .9)',
        color: 'white',
        height: '200px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        borderRadius: '5% 5% 0 0',
        padding: '5px 3px 0 3px',
        position: 'relative',
        top: '35px',
    }

    slider.top = slideUp ? '35px' : '210px';

    return (
        <div style={ imageCard } id={props.keyx}>
            <div style={ slider }>

                <div style={ headingBl }>{props.heading}</div>

                <div style={ textBl }>{props.text}</div>

                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>

            </div>
        </div>
      );

}

export default ImageCard;
