import React from 'react';
import Modal from './Modal';

function Home() {

    const homeBannerImg = {
        backgroundImage: 'url("/images/home3.png")',
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        height: '30vh',
    }

    const bannerTxt = {
        color: '#fff',
        margin: '0',
        fontFamily: 'arial, san-serif',
        fontSize: '4.5vmin',
        fontWeight: '400',
        textAlign: 'left',
    }

    const buttonCont = {
        display: 'inline-block',
        float: 'left',
        margin: '1vmin 0 0 0',
    }

    const btn = {
        height: '3vmin',
        fontSize: '1.4vmin',
        border: 'none',
        color: 'white',
        background: 'rgb(245,246,250)',
        background: 'radial-gradient(circle, rgba(245,246,250,1) 0%, rgba(26,56,111,0.6) 85%)',
    }

    const textCont1 = {
        background: 'rgba(26,56,111,.5)',
        padding: '60px 0 20px 50px',
        flex: '1',

    }
    const textCont2 = {
        display: 'block',
        background: 'rgba(100,100,100,.5)',
        flex: '1',
    }

  return (
    <>    
        <div className="content">
            <div>
                <div className="banner" style={ homeBannerImg }>
                    <div style={ textCont1 }>
                        <h2 style={ bannerTxt }>
                            Restoration.
                            <br/>
                            <b style={{fontWeight: '600'}}>Repair.</b>
                            <br/>
                            Waterproofing. 
                        </h2>
                        <div style={ buttonCont }>
                            <button style={ btn }>Our Services</button>
                        </div>
                    </div>
                    <div style={ textCont2 }></div>
                </div>
            </div>

            {/* Here I need to grab the data in dataFeed.json and map over it to create multiple modals */}
            <Modal image="/images/construction2.jpg" heading="Check it out!" text="This was a project we completed in cooperation with Hilton."/>
        </div>
    </>
  );
}

export default Home;
