import React from 'react';

// Styles
const homeBannerImg = {
    backgroundImage: 'url("/images/home.png")',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    height: '30vh',
    margin: '15px 0 0 0',
}

const bannerTxt = {
    color: '#fff',
    margin: '5vw 0 3vw 0',
    fontSize: '4.5vw',
    fontWeight: '100',
    textAlign: 'left',
    lineHeight: '1em',
}

const buttonCont = {
    display: 'inline-block',
    float: 'left',
    margin: '1vw 0 0 0',
}

const btn = {
    height: '3vw',
    fontSize: '1.4vw',
    border: 'none',
    color: 'white',
    background: 'radial-gradient(circle, rgb(166, 25, 46) 0%, rgba(26,56,111,0) 100%)',
}

const textCont1 = {
    background: 'rgba(26,56,111,.5)',
    padding: '6vw 0 20px 6vw',
    flex: '1',

}

function ImageBanner(props) {

    return (
        <div className="banner" style={ homeBannerImg }>
            <div style={ textCont1 }>
                <h2 style={ bannerTxt }>
                    {props.line1}
                    <br/>
                    <b style={{fontWeight: '600'}}>{props.line2}</b>
                    <br/>
                    {props.line3} 
                </h2>
                {
                    props.buttonLink ? 
                    <div style={ buttonCont }>
                        <a href={props.buttonLink}>
                            <button style={ btn }>{props.buttonText}</button>
                        </a>
                    </div>
                    :null
                }
            </div>
        </div>
    )
}

export default ImageBanner;
