import React from 'react';

function Modal(props) {

    const modalContainer = {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        height: '40vmin',
        width: '30vmin',
        backgroundColor: '#ddf',
        margin: '10px',
        border: '1px solid black',
        // borderRadius: '10px',

    }

    const imageContainer = {
        width: '100%',
        flex: 2,
    }

    const heading = {
        margin: '5px',
        flex:1,
    }

    const text = {
        margin: '0 8px 10px 8px',
        flex: 1,
    }

  return (
    <>    
        <div style={ modalContainer }>
            <div style={ imageContainer }>
                <img src={props.image} alt="Modal Image" width="100%" />
            </div>
            <h2  style={ heading }>
                {props.heading}
            </h2>
            <div  style={ text }>
                {props.text}
            </div>
        </div>
    </>
  );
}

export default Modal;
