import React from 'react';

const slider = {
    background: 'rgba(44,42,41)',
}

const headingBl = {

}

const textBl = {

}

const buttonCont = {
    padding: '0 0 10px 0',
}

const button = {
    background: 'rgb(166, 25, 46)',
    color: 'white',
    fontSize: '1em',
    fontWeight: '600',
    height: '50%',
    minHeight: '25px',
    padding: '0 20%',
    border: 'none',
    borderRadius: '5px',
}

function ImageCard(props) {


    var imageCard = {
        background: `url('${props.image || './images/placeholder.jpg'}')`,
        width: '180px',
        height: '240px',
    }

    return (
        <div style={ imageCard }>
            <div style={ slider }>

                <div style={ headingBl }>{props.heading}</div>

                <div style={ textBl }>{props.text}</div>

                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>

            </div>
        </div>
      );

}

export default ImageCard;
