import React from 'react';
import glamorous from 'glamorous';

function ContactCard(props) {

    const contactCard = {
        display: 'flex',
        justifyContent: 'space-between',
        width: '100%',
        height: '100%',
    }

    const ImgCont = glamorous.div(
        {
            background: `url(${props.image})`,
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
            // display: 'block',
            width: '19vw',
            // borderRadius: '50%',
            position: 'relative',
            border: '2px solid rgb(166, 25, 46)',
            '&:after': {
                content: '',
                display: 'block',
                paddingBottom: '100%',
            }
        },
    )

    const contDesc = {
        height: '100%',
        width: '60%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
    }

    const nameDesc = {
        display: 'flex',
        flexDirection: 'column',
        margin: '10px 0 0 0'
    }

    const contactDesc = {

        fontSize: '3vw',
        fontWeight: '600',
        letterSpacing: '1px',
        margin: '4vh 0 5vh 0',
    }


    return ( 
        <>
            <div styles={ contactCard }>
                <ImgCont>
                    <img src={props.image} alt="CBSI Employee" />
                </ImgCont>
                <div styles={ contDesc }>
                    <div styles={ nameDesc }>
                        <div styles={{fontWeight: '600',fontSize: '2.5vw'}}> {props.name} </div>
                        <div styles={{fontSize: '10px'}}> {props.title} </div>
                    </div>
                    <div styles={contactDesc}>
                        {props.email}
                    </div>
                </div>
            </div>
        </>
    );
}

export default ContactCard;
