import React from 'react';

const modalContainer = {
    background: 'rgba(63, 73, 83, 1)',
    minHeight: '300px',
    minWidth: '250px',
    height: '40vw',
    maxHeight: '450px',
    color: '#fff',
    margin: '0 20px 0 20px',
    borderRadius: '5px',
    display: 'flex',
    flexDirection: 'column',
}

const contentContainer = {
    height: '60%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    padding: '0 3px',
}

const heading = {
    fontSize: '120%',
    margin: 0,
    padding: '5px',
    color: '#fff',
}

const text = {
    flex: '2',
    fontSize: '95%',
    color: '#fff',
}

const buttonCont = {
    padding: '0 0 10px 0',
}

const button = {
    background: 'rgb(166, 25, 46)',
    color: 'white',
    fontSize: '1em',
    fontWeight: '600',
    height: '50%',
    minHeight: '25px',
    padding: '0 20%',
    border: 'none',
    borderRadius: '5px',
}

function Modal(props) {

    const imageContainer = {
        width: '99%',
        height: '40%',
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        border: '1px solid #151C29',
        borderTopLeftRadius: 'inherit',
        borderTopRightRadius: 'inherit',
    }
    const bg = {
        background: `url(${props.image})`,
    }

  return (
    <>    
        <div style={ modalContainer }>
            <a href={props.link} style={ {...imageContainer,bg} }>&nbsp;</a>  
            <div style={ contentContainer }>
                <h1  style={ heading }>
                    {props.heading}
                    <hr style={{color: 'white'}}/>
                </h1>
                <div  style={ text }>
                    {props.text}
                </div>
                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
