import React from 'react';
import Modal from './Modal';

export class ScrollableArea extends React.Component {

    render() {

        const scAreaHeading = {
            color: 'white',
            fontSize: '5vw',
            fontWeight: '100',
            letterSpacing: '2px',
            margin: '20px 0 0 0',
            WebkitTextStrokeWidth: '1px',
            WebkitTextStrokeColor: 'rgba( 124, 146, 160, 1 )',
            textShadow: '1px 1px rgba( 0, 0, 0, .3 )',

        }

        const scrollableAreaCont = {
            // backgroundColor: '#eee',
            background: 'rgb(164,170,182)',
            background: 'linear-gradient(4deg, rgba(164,170,182,0.5) 0%, rgba(195,212,223,0.8) 100%)',
            display: 'block',
            overflowX: 'scroll',
            overflowY: 'none',
            minHeight: '250px',
            overflow: 'auto',
            padding: '15px 0 15px 0',
            margin: '5px 10px 40px 10px',
            borderRadius: '3px',

        }

        const scrollableAreaContFlex = {
            display: 'flex',
            justifyContent: 'space-between',
            height: 'inherit',
        }

        var dataArr = [];

        Object.keys(this.props.jsonData).forEach(key => dataArr.push(this.props.jsonData[key]));

        return (
            <>
                <h1 style={ scAreaHeading }>{this.props.heading}</h1>
                <div style={ scrollableAreaCont }>
                    <div style={ scrollableAreaContFlex }>
                        {
                            dataArr.map(item => 
                                <Modal 
                                    key={ item.id }
                                    image={ item.image } 
                                    heading={ item.heading } 
                                    text={ item.text } 
                                    buttonTxt={ item.buttonTxt } 
                                    link={ item.link }
                                />
                            )
                        }
                    </div>
                </div>
            </>
        )
    }
}

export default ScrollableArea;
