import React from 'react';

const basicInfoBlock = {
    background: 'cyan',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
}

const headingCont = {
    fontSize: '4vw',
}

const subHeadingCont = {

}

const textCont = {
    fontSize: '2vw',
    lineHeight: '3vw',
    padding: '0 10vw',
}

function BasicInfoBlock(props) {

    return (
        <div style={ basicInfoBlock } className="basicInfoBlock">
            <h1 style={ headingCont }> 
                {props.heading || 'Heading Prop Required'} 
            </h1>
            <h1 style={ subHeadingCont }> 
                { props.subHeading } 
            </h1>
            <div style={ textCont }>
                {props.text || 'Text Prop Required'}
            </div>
        </div>
    );
}

export default BasicInfoBlock;
