import React from 'react';

var imageInfoBlock = {
    height: '30vw',
    display: 'flex',
    justifyContent: 'center',
}

var dark = {
    background: 'rgba(44,42,41)',
    color: 'white',
}

var img = {
    height: '100%',
}

var textBl = {
    // background: 'cyan',
    flex: 2,
    display: 'flex',
    flexDirection: 'column',
    padding: '3vw',
    textAlign: 'left',
}

var headerContBlock = {
    // background: 'green',
    fontSize: '3vw',
    fontWeight: 'bold',
    margin: '0 0 2.5vw 0',
    padding: '0 0 0 1.2vw',
}

var textContBlock = {
    // background: 'orange',
    width: '100%',
    height: '100%',
    fontSize: '1.8vw',
    display: 'flex',
}

function ImageInfoBlock(props) {
    color = props.isDark ? {} : {};

    if(props.flip) {
        imageInfoBlock = {...imageInfoBlock, flexDirection: 'row-reverse'}
    }

    return (
        <div style={ {...imageInfoBlock, ...color} } className="imageInfoBlock">
            <img src={ props.image } alt={props.headding} style={ img } />
            <div style={ textBl }>
                <div style={headerContBlock}>
                    {props.headerContent}
                </div>
                <div style={textContBlock}>
                    {props.textContent}
                </div>
            </div>
        </div>
    );
}

export default ImageInfoBlock;
