import React from 'react';
import Modal from './Modal';

const responsiveGrid = {
    backgroundColor: '#2196F3',
    display: 'grid',
    gridTemplateColumns: 'repeat( auto-fit, minmax(300px, 1fr) )',
    width: '90%',
    margin: 0,
    padding: '5vw',
}

const gridItem = {
    background: 'yellow',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '1vw 0 4vw 0',
}

function ResponsiveGrid(props) {

    var dataArr = [];

    //  Grabs data from the props.jsonData file and pushes each modal object into the dataArr array.
    /*  
        I feel like this needs an explanation:
            This was done so that later on, I can set up a CMS for CBSI to update their site more easily. Eventually, Ill populate
            that props.jsonData file using CMS options for any user grated such privelages. For now, it doesnt cause  any
            performance issues, and will stay this way until the CMS portion is completed.

            The modals are currently entirely created and populated using data from the props.jsonData file. 
    */
    Object.keys(props.jsonData).forEach(key => dataArr.push(props.jsonData[key]));

    console.log(dataArr);

    return (
        <div style={ responsiveGrid } className="responsiveGrid">
            {
                //Output a Modal component for each object in dataArr
                dataArr.map(item => 
                    <div style={ gridItem }>
                        <Modal 
                            key={ item.id }
                            image={ item.image } 
                            heading={ item.heading } 
                            text={ item.text } 
                            buttonTxt={ item.buttonTxt } 
                            link={ item.link }
                        />
                    </div>
                )
            }
        </div>
    );
}

export default ResponsiveGrid;
