import React, {useState, useEffect} from 'react';

const basicInfoBlock = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '8vw 0',
}

const headingCont = {
    color: 'rgb(166, 25, 46)',
    fontSize: '3.5vw',
    fontWeight: '300',
    padding: '1vw 2vw 3vw 2vw',
    wordSpacing: '2vw',
}

const subHeadingCont = {
    fontSize: '2.5vw',
    fontWeight: '500',
    padding: '1vw 1vw 3vw 1vw',
    width: '90vw',
}

const textCont = {
    fontSize: '2vw',
    fontWeight: '300',
    width: '90vw',
    lineHeight: '3vw',
    padding: '1vw',
}

function BasicInfoBlock(props) {
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);

    const handleResize = () => {
        setWindowWidth(window.innerWidth);
    }

    useEffect(() => {
        window.addEventListener('resize', handleResize);
    }, [])

    var basicInfoBlockR;
    var headingContR;
    var subHeadingContR;
    var textContR;
    if(windowWidth < 600){
        headingContR = {
            color: 'rgb(166, 25, 46)',
            fontSize: '6vw',
            fontWeight: '300',
            padding: '1vw 2vw 3vw 2vw',
            wordSpacing: '2vw',
        }
        subHeadingContR = {
            fontSize: '3.5vw',
            fontWeight: '500',
            padding: '1vw 1vw 3vw 1vw',
            width: '90vw',
        }
        textContR ={
            fontSize: '3.5vw',
            fontWeight: '300',
            width: '90vw',
            lineHeight: '5vw',
            padding: '1vw',
        }
    }else if(windowWidth > 1280) {

        basicInfoBlockR = {
            background: 'red',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            padding: '6vw 200px',
        }
        headingContR = {
            background: 'green',
            color: 'rgb(166, 25, 46)',
            fontSize: '4vw',
            fontWeight: '300',
            padding: '1vw 2vw 3vw 2vw',
            wordSpacing: '2vw',
        }
        subHeadingContR = {
            background: 'cyan',
            fontSize: '2.5vw',
            fontWeight: '500',
            padding: '1vw 0 3vw 0',
            width: '90vw',
        }
        textContR ={
            background: 'orange',
            fontSize: '2vw',
            fontWeight: '300',
            lineHeight: '3vw',
            padding: '1vw',
        }

    }

    return (
        <div style={ basicInfoBlockR || basicInfoBlock } className="basicInfoBlock">
            <div style={ headingContR || headingCont }> 
                { props.heading || 'Heading Prop Required' } 
            </div>
            <div style={ subHeadingContR || subHeadingCont }> 
                { props.subHeading } 
            </div>
            <div style={ textContR || textCont }>
                { props.text }
            </div>
        </div>
    );
}

export default BasicInfoBlock;
