import React, {useState, useEffect} from 'react';

// Styles

const homeBannerImg = {
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    height: '60vw',
    margin: '0 0 0 0',
}

const textCont1 = {
    background: 'rgba(26,56,111,.5)',
    padding: '10vw 0 0 6vw',
    flex: '1',

}

function ImageBanner(props) {
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);

    const handleResize = () => {
        setWindowWidth(window.innerWidth);
    }

    useEffect(() => {
        window.addEventListener('resize', handleResize);
    }, [])

    homeBannerImg = {
        ...homeBannerImg,
        backgroundImage: `url('${props.image}')`,
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        minHeight: '50vw',
        overflow: 'hidden',
    }

    // var homeBannerImgR;
    // var imgR;
    // var textCont1R;
    // if(windowWidth > 600 && windowWidth < 1280){    // Styling for tablets & small laptops

    // }else if(windowWidth >= 1280) {     // Styling for Desktop
    //     homeBannerImgR = {
    //         backgroundPosition: 'center',
    //         backgroundSize: 'cover',
    //         height: '60vw',
    //         margin: '0 0 0 0',
    //     }
    //     imgR = {
    //         backgroundImage: `url('${props.image}')`,
    //         backgroundRepeat: 'no-repeat',
    //         display: 'flex',
    //         minHeight: '50vw',
    //         overflow: 'hidden',
    //     }

    //     textCont1R = {
    //         background: 'rgba(26,56,111,.5)',
    //         padding: '10vw 0 0 6vw',
    //         flex: '1',
    //     }
    // }

    return (
        <div style={ homeBannerImg }>
            <div style={ textCont1 }>
                <h2 className="bannerTxt">
                    {props.line1}
                    <br/>
                    <b style={{fontWeight: '600'}}>{props.line2}</b>
                    <br/>
                    {props.line3} 
                </h2>
                {
                    props.buttonLink ? 
                    <div className="buttonCont">
                        <a href={props.buttonLink}>
                            <button className="bannerBtn">{props.buttonText}</button>
                        </a>
                    </div>
                    :null
                }
            </div>
            <div className="textCont2"></div>
        </div>
    )
}

export default ImageBanner;
