import React, { useState } from 'react';
import ImageInfoBlock from './ImageInfoBlock';

const dataListBlock = {

}

function DataListBlock(props) {
    const [isDark, setIsDark] = useState(false);
    const [isFlipped, setIsFlipped] = useState(false);

    var dataArr = [];
    Object.keys(props.jsonData).forEach(key => dataArr.push(props.jsonData[key]));


    return (
        <div style={ dataListBlock } className="dataListBlock">
            <h1>MORE INFO</h1>

            {
                //Output a Modal component for each object in dataArr
                dataArr.map((item, i) => {

                    return (
                        <ImageInfoBlock 
                            isDark={ i%2==0 } 
                            flip={ i%2==0  }
                            image={item.image} 
                            headerContent={item.heading} 
                            textContent="
                                Our safety-oriented focus on customer service and quality craftsmanship has been 
                                valued by our customers. We’ve had the unique privilege of working on some of the 
                                most prestigious commercial, multi-family and historic government properties in the 
                                Washington, D.C. metropolitan area.  Click on the “View Projects” button below to see 
                                a comprehensive list of our projects.
                            " 
                        />
                    )
                })
            }
        </div>
    );
}

export default DataListBlock;
