import React, {useState} from 'react';
import { useForm } from 'react-hook-form';

const emailFormCont = {
    // background: 'red',
}

const errorMsgBlock = {
    background: 'rgba(255, 0, 0, .25)',
    padding: '.5vw',
    borderRadius: '5px',
    margin: '0 0 2vw 0',
    display: 'inline-block',
}
const error = {
    color: 'red',
    fontSize: '2vw',
    fontWeight: '400',
    margin: '.5vw',
}

const heading = {
    fontSize: '3vw',
    fontWeight: '400',
    margin: '2vw 0',
}

const form = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
}

const textInput = {
    background: '#eee',
    color: 'black',
    width: '40vw',
    height: '4vw',
    margin: '0 0 3vw 0',
    fontSize: '2vw',
    border: '2px solid black',
    borderRadius: '10px',
    padding: '0 1vw 0 1vw',
}
const textarea = {
    background: '#eee',
    color: 'black',
    resize: 'none',
    width: '38vw',
    margin: '0 0 3vw 0',
    fontSize: '2vw',
    border: '2px solid black',
    borderRadius: '10px',
    padding: '1vw 1vw 1vw 1vw',
}

const submitBtn = {
    background: 'rgb(166, 25, 46)',
    color: 'white',
    width: '15vw',
    margin: '0 0 3vw 0',
    fontSize: '2vw',
    border: '2px solid black',
    borderRadius: '10px',
}

function EmailForm(props) {
    const {register, handleSubmit, errors} = useForm();

    // const errorDiv = document.getElementById('errorBlock');
    const nameInput = document.getElementById('name');
    const emailInput = document.getElementById('email');
    // const subjectInput = document.getElementById('subject');
    const messageInput = document.getElementById('message');

    const onSubmit = (data) => {
        console.log(data);
        document.getElementById("emailForm").reset();  // Clear form fields
    }

    const showError = (errorMessage, formInput) => {
        formInput.style.background = 'rgba(255, 0, 0, .2)';
        return <div style={ error }>{errorMessage}</div>
    }

    return (
        <div style={ emailFormCont } className="emailFormCont">
            <div style={ heading }>{ props.heading }</div>
            { Object.keys(errors).length > 0 &&
                <div style={ { ...errorMsgBlock } } id="errorBlock">
                    {errors.name && showError(errors.name.message, nameInput)}
                    {errors.email && showError("Invalid Email", emailInput)}
                    {errors.message && showError(errors.message.message, messageInput)}
                </div> 
            }
            <form onSubmit={handleSubmit(onSubmit)} style={ form } id="emailForm">

                {/* Name Input*/}
                <input 
                    type="text" 
                    placeholder="Name" 
                    name="name" 
                    id="name"
                    ref={register({required: "Name Required"})} 
                    style={ textInput }
                />

                {/* Email Input*/}
                <input 
                    type="text" 
                    placeholder="Email" 
                    name="email" 
                    id="email"
                    ref={register({ required: "Email Required", pattern: /^([a-zA-Z0-9_\-.]+)@([a-zA-Z0-9_\-.]+)\.([a-zA-Z]{2,5})$/i })} 
                    style={ textInput }
                />

                {/* Subject Input*/}
                <input 
                    type="text" 
                    placeholder="Subject" 
                    name="subject" 
                    id="subject"
                    ref={register} 
                    style={ textInput }
                />

                {/* Message Input*/}
                <textarea  
                    type="text" 
                    placeholder="Message" 
                    name="message" 
                    id="message"
                    rows="4"
                    ref={register({required: "Message Required"})} 
                    style={ textarea }
                />

                {/* Submit Button */}
                <input type="submit" name="submit" value="SEND" style={ submitBtn }/>

            </form>
        </div>
    );
}

export default EmailForm;
