import React from 'react';
import ScrollableArea from './components/ScrollableArea';
import ContactBlock from './components/ContactBlock';
import Footer from './components/Footer';
import ScalableInfoBlock from './components/ScalableInfoBlock';
// Data imports
import ProjectsData from './data/projects.json';
import InfoData from './data/homeInfoBlockData.json';


const homeBannerImg = {
    backgroundImage: 'url("/images/home.png")',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    height: '30vh',
    margin: '15px 0 0 0',
}

const bannerTxt = {
    color: '#fff',
    margin: '5vw 0 3vw 0',
    fontSize: '4.5vw',
    fontWeight: '100',
    textAlign: 'left',
    lineHeight: '1em',
}

const buttonCont = {
    display: 'inline-block',
    float: 'left',
    margin: '1vw 0 0 0',
}

const btn = {
    height: '3vw',
    fontSize: '1.4vw',
    border: 'none',
    color: 'white',
    background: 'radial-gradient(circle, rgb(166, 25, 46) 0%, rgba(26,56,111,0) 100%)',
}

const textCont1 = {
    background: 'rgba(26,56,111,.5)',
    padding: '6vw 0 20px 6vw',
    flex: '1',

}
const textCont2 = {
    display: 'block',
    background: 'rgba(100,100,100,.5)',
    flex: '1',
}

function Home() {

  return (
    <>    
        <div className="content">
            <div className="banner" style={ homeBannerImg }>
                <div style={ textCont1 }>
                    <h2 style={ bannerTxt }>
                        Restoration.
                        <br/>
                        <b style={{fontWeight: '600'}}>Repair.</b>
                        <br/>
                        Waterproofing. 
                    </h2>
                    <div style={ buttonCont }>
                        <a href="/services">
                            <button style={ btn }>View All Services</button>
                        </a>
                    </div>
                </div>
                <div style={ textCont2 }></div>
            </div>

            {/* This component is responsible for the main text block in the home page ('plan build manage')*/}
            <ScalableInfoBlock jsonData={ InfoData }/>

            <ScrollableArea heading="Projects Spotlight" jsonData={ ProjectsData }/>

            <ContactBlock/>

            <Footer />

        </div>
    </>
  );
}

export default Home;
