import React from 'react';

var imageInfoBlock = {
    display: 'flex',
    justifyContent: 'center',
    padding: '6vw 3vw 3vw 3vw',
}

var imgCont = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
}

var img = {
    height: '27vw',
    width: '40vw',
    borderRadius: '3px',
}

var textBl = {
    flex: 2,
    display: 'flex',
    flexDirection: 'column',
    textAlign: 'left',
    padding: '0 3vw 3vw 3vw 3vw',
}

var headerContBlock = {
    // background: 'green',
    fontSize: '3vw',
    fontWeight: 'bold',
    margin: '0 0 2.5vw 0',
    padding: '0 0 0 1.2vw',

}

var textContBlock = {
    // background: 'yellow',
    width: '90%',
    height: '85%',
    fontSize: '1.8vw',
    display: 'flex',
    margin: '0 0 0 2.3vw',
    // overflow: 'scroll',
}

function ImageInfoBlock(props) {

    var color = props.isDark 
        ? { background: 'rgba(63, 73, 83, 1)', color: 'white' } 
        : { background: '', color: 'rgba(44,42,41)' };

    var flip = props.flip
        ? { flexDirection: 'row-reverse' }
        : { flexDirection: 'row' };

    return (
        <div style={ {...imageInfoBlock, ...color, ...flip} } className="imageInfoBlock" id={props.id}>
            <div style={ imgCont }><img src={ props.image || './images/placeholder.jpg' } alt={props.headding} style={ img } /></div>
            <div style={ textBl }>
                <div style={headerContBlock}>
                    {props.headerContent}
                </div>
                <div style={textContBlock}>
                    {props.textContent}
                </div>
            </div>
        </div>
    );
}

export default ImageInfoBlock;
