import React from 'react';
import { Blockquote } from 'glamorous';

export class ScalableInfoBlock extends React.Component {

    render() {
        var dataArr = [];
        Object.keys(this.props.jsonData).forEach(key => dataArr.push(this.props.jsonData[key]));
        var numSections = dataArr.length;

        const scalableInfoBlock = {
            background: `${this.props.bgColor}`,
            display: 'flex',
            margin: '3vw 4vw',
        }

        const bl = {
            // backgroundColor: 'cyan',
            width: '100%',
            height: '30vw',
            margin: '1vw 0',
            padding: '.5vw',
            display: 'flex',
            flexDirection: 'column',
            borderRight: '1px solid black',
        }

        const heading = {
            fontSize: '2.5vw',
            fontWeight: '600',
            color: 'rgb(166, 25, 46)',
            margin: '1vw',
        }

        const text = {
            fontSize: '1.5vw',
            margin: '1vw',
            flex: 2,
        }

        const btnTxtBl = {
            display: 'flex',
            alignItems: 'center',
        }

        const button = {
            display: 'block',
            background: `url('/images/arrowIcon.png')`,
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
            width: '3.5vw',
            height: '3.5vw',
            margin: '0 0 0 1vw',
        }

        const buttonText = {
            margin: '0 0 0 1vw ',
            fontSize: '1.7vw',
        }

        function blockRender(item, i) {
            var divPicker = '<div style={ bl } key={item.id}>';
            if(i == dataArr.length){
                // divPicker = <div style={ bl } key={item.id}>;
            }
            return(
                <div style={ bl } key={item.id}>
                    <div style={ heading }>
                        {item.heading}
                        <hr/>
                    </div>
                    <div style={ text }>
                        {item.text}
                    </div>
                    <div style={ btnTxtBl }>
                        <div style={ button }></div>
                        <div style={ buttonText }>{ item.buttonTxt }</div>
                    </div>
                </div>
            );
        }

        return (
            <div style={ scalableInfoBlock } className="scalableInfoBlock">
                {
                    dataArr.map((item, i) => 
                        blockRender(item, i)

                    )
                }
            </div>
        );
    }
}

export default ScalableInfoBlock;
