import React from 'react';
import { useForm } from 'react-hook-form';

const emailForm = {
    background: 'red',
}

const errorMsgBlock = {

}

const heading = {
    fontSize: '3vw',
    fontWeight: '400',
    margin: '2vw 0',
}

const formCont = {
}

const form = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
}

const textInput = {
    width: '50vw',
    height: '4vw',
    margin: '0 0 3vw 0',
    fontSize: '2vw',
    borderRadius: '10px',
}
const textarea = {
    width: '38vw',
}

const submitBtn = {

}

function EmailForm(props) {
    const {register, handleSubmit, errors} = useForm();

    const onSubmit = (data) => {
        console.log(data);
    }

    return (
        <div style={ emailForm } className="emailForm">
            <div style={ heading }>{ props.heading }</div>
            <div style={ errorMsgBlock }>
                {errors.email && <p>Invalid email</p>}
                {errors.name && <p>{errors.name.message}</p>}
            </div>
            <div style={ formCont }>

                <form onSubmit={handleSubmit(onSubmit)} style={ form }>

                    {/* Name Input*/}
                    <input 
                        type="text" 
                        placeholder="Name" 
                        name="name" 
                        ref={register({required: "Name Required"})} 
                        style={ textInput }
                    />

                    {/* Email Input*/}
                    <input 
                        type="text" 
                        placeholder="Email" 
                        name="email" 
                        ref={register({ pattern: /^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/i })} 
                        style={ textInput }
                    />

                    {/* Subject Input*/}
                    <input 
                        type="text" 
                        placeholder="Subject" 
                        name="subject" 
                        ref={register} 
                        style={ textInput }
                    />

                    {/* Message Input*/}
                    <textarea  
                        type="text" 
                        placeholder="Message" 
                        name="Message" 
                        rows="4"
                        ref={register({required: "Message Required"})} 
                        style={ textarea }
                    />

                    {/* Submit Button */}
                    <input type="submit" style={ submitBtn }/>

                </form>

            </div>
        </div>
    );
}

export default EmailForm;
