import React, { useState } from 'react';

const modalContainer = {
    background: 'rgba(44,42,41)',
    height: '350px',
    width: '275px',
    color: '#fff',
    border: '1px solid black',
    borderRadius: '5px',
    display: 'flex',
    flexDirection: 'column',
    overflow: 'hidden',
}

const imageContainer = {
    width: '99%',
    height: '40%',
    borderTopLeftRadius: 'inherit',
    borderTopRightRadius: 'inherit',
}

const img = {
    width: '230%',
    height: '250%',
    minHeight: '140px',
}

const contentContainer = {
    background: 'rgba(44,42,41, .8)',
    position: 'relative',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    padding: '0 3px',
}

const heading = {
    fontSize: '120%',
    margin: 0,
    padding: '5px',
    color: '#fff',
}

const headingText = {
    whiteSpace: 'nowrap',
    overflow: 'none',

}

const text = {
    flex: '2',
    fontSize: '95%',
    color: '#fff',
    margin: '',
}

const buttonCont = {
    padding: '0 0 10px 0',
}

const button = {
    background: 'rgb(166, 25, 46)',
    color: 'white',
    fontSize: '1em',
    fontWeight: '600',
    height: '50%',
    minHeight: '25px',
    padding: '0 20%',
    border: 'none',
    borderRadius: '5px',
}

function Modal(props) {
    const [infoHeight, setInfoHeight] = useState('175px');

    function hoverHandler() {
        if(infoHeight === '0px'){
            setInfoHeight('175px')
        } else {
            setInfoHeight('0px')
        }
    }

    function checkOverflow(e) {
        var item = document.getElementById(`${e}`);
        console.log(item);
    }

  return (
    <>    
        <div style={ modalContainer } className="modal" id={props.id} onMouseEnter={hoverHandler} onMouseLeave={hoverHandler}>
            <a href={props.link} style={ imageContainer }>
                <img src={ props.image } alt={props.heading} style={ img } />    
            </a>  
            <div style={ {...contentContainer, top: infoHeight } }>
                <h1  style={ heading } id={'h'+props.id} onLoad={ checkOverflow('h'+props.id) } >
                    <div style={ headingText }>{props.heading}</div>
                    <hr style={{color: 'white'}}/>
                </h1>
                <div  style={ text }>
                    {props.text}
                </div>
                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
