import React from 'react';

const quoteBlock = {
    fontFamily: 'handWritten2, Open Sans, sans-serif',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '3vw'
}

const quoteCont = {
    zIndex: '-1',
    background: 'white',
    border: '1px solid rgb(166, 25, 46)',
    maxWidth: '75%',
    position: 'relative',
}

const leftQuoteImg = {
    // background: 'green',
    height: '6vw',
    position: 'absolute',
    top: '-2.8vw',
    left: 0,
}

const quoteText = {
    // background: 'orange',
    fontFamily: 'handWritten2, Ariel, serif',
    fontSize: '3vw',
    margin: '1.8vw 10vw',
    lineHeight: '3.5vw',
}

const rightQuoteImg = {
    // background: 'black',
    height: '6vw',
    position: 'absolute',
    bottom: '-2.8vw',
    right: 0,
}
const quoteAuthorBlock ={
    display: 'flex',
    alignItems: 'center',
    margin: '1vw 0 0 0'
}

const authorTxt = {
    fontSize: '2vw',
    fontWeight: '600',
}

const authorDesc = {
    fontSize: '2vw',
    margin: '0 0 0 1vw',
}

function QuoteBlock(props) {
    return (
        <div style={ quoteBlock } className="quoteBlock">
            <div style={ quoteCont }>
                <img src="./images/leftQuote.png" alt="construction" style={ leftQuoteImg } />
                <div style={ quoteText}>
                    { props.quote || 'quote Required' }
                </div>
                <div style={rightQuoteImg}></div>
                <img src="./images/rightQuote.png" alt="construction" style={ rightQuoteImg } />
            </div>
            <div style={quoteAuthorBlock}>
                <div style={ authorTxt } >- {props.author}</div>
                <div style={ authorDesc } >({props.authorDesc})</div>
            </div>
        </div>
    );
}

export default QuoteBlock;
