import React from 'react';
import { withRouter } from 'react-router-dom';

function Footer() {

    const footer = {
        // backgroundColor: 'rgba(1,23,37,.8)',
        background: 'rgb(1,0,20)',
        background: 'linear-gradient(-180deg, rgba(1,0,20,.5) 51%, rgba(12,29,60,.3) 100%)',

        color: 'white',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '10px 20px 0 20px',

    }
    const copyright = {
        fontSize: '10px',
        fontWeight: '100',
        letterSpacing: '2px',

    }

    return (
        <div style={ footer }>
            <div>
                <img src="/images/cbsiLogo.png" alt="CBSI Logo" width="100vw"/>
            </div>

            <p style={ copyright }>Copyright Â© 2020 CBSI. All rights reserved.</p>
        </div>
    );
}

export default Footer;
