import React from 'react';

function Modal(props) {

    const modalContainer = {
        backgroundColor: '#2f3538',
        minHeight: '300px',
        minWidth: '250px',
        height: '40vw',
        maxHeight: '450px',
        color: '#fff',
        margin: '0 20px 0 20px',
        borderRadius: '5px',
        display: 'flex',
        flexDirection: 'column',
        border: '1px solid gray',
    }

    const imageContainer = {
        width: '100%',
        height: '40%',
        background: `url(${props.image})`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        borderTopLeftRadius: 'inherit',
        borderTopRightRadius: 'inherit',
    }

    const contentContainer = {
        height: '60%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',

    }

    const heading = {
        // backgroundColor: 'red',
        fontSize: '150%',
        margin: 0,
        padding: '5px',
        color: '#fff',
        textShadow: '1px 1px rgba( 0, 0, 0, .5 )',
    }

    const text = {
        // backgroundColor: 'green',
        flex: '2',
        fontSize: '100%',
        padding: '8px',
        color: '#fff',
    }

    const buttonCont = {
        // backgroundColor: 'cyan',
        padding: '10px',
    }

    const button = {
        background: 'white',
        backgroundColor: 'rgba(41,40,60,1)',
        color: 'white',
        fontSize: '1em',
        fontWeight: '600',
        border: '1px solid lightgray',
        height: '50%',
        minHeight: '25px',
        padding: '0 20%',
        borderRadius: '5px',
    }

  return (
    <>    
        <div style={ modalContainer }>
            <div style={ imageContainer }></div>
            <div style={ contentContainer }>
                <h1  style={ heading }>
                    {props.heading}
                </h1>
                <div  style={ text }>
                    {props.text}
                </div>
                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
