import React from 'react';

const slider = {
    background: 'rgba(44,42,41)',
    color: 'white',
    height: '200px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    borderRadius: '5% 5% 0 0',
    padding: '5px 3px 0 3px'
}

const headingBl = {
    fontSize: '18px',
}

const textBl = {
    fontSize: '.9em',
}

const buttonCont = {
    padding: '0 0 10px 0',
}

const button = {
    background: 'rgb(166, 25, 46)',
    color: 'white',
    fontSize: '.9em',
    fontWeight: '600',
    height: '20px',
    padding: '0 5px',
    border: 'none',
    borderRadius: '5px',
}

function ImageCard(props) {


    var imageCard = {
        background: `url('${props.image || './images/placeholder.jpg'}')`,
        width: '180px',
        height: '240px',
    }

    return (
        <div style={ imageCard }>
            <div style={ slider }>

                <div style={ headingBl }>{props.heading}</div>

                <div style={ textBl }>{props.text}</div>

                <div  style={ buttonCont }>
                    <a href={props.link}>
                        <button style={ button }>{ props.buttonTxt || "Button" }</button>
                    </a>
                </div>

            </div>
        </div>
      );

}

export default ImageCard;
