import React from 'react';

function Modal(props) {

    const modalContainer = {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '40vmin',
        width: '30vmin',
        minHeight: '300px',
        minWidth: '200px',
        backgroundColor: 'rgba(26,56,111,.8)',
        color: '#fff',
        margin: '10px',

    }

    const imageContainer = {
        width: '100%',
        overflow: 'none',
    }

    const heading = {
        margin: '5px',
        fontSize: '3vh',
    }

    const text = {
        fontSize: '2vmin',
        margin: '0 8px 10px 8px',
        flex: 1,
    }

  return (
    <>    
        <div style={ modalContainer }>
            <div style={ imageContainer }>
                <img src={props.image} alt="Modal" width="100%" />
            </div>
            <h2  style={ heading }>
                {props.heading}
            </h2>
            <div  style={ text }>
                {props.text}
            </div>
        </div>
    </>
  );
}

export default Modal;
