import React from 'react';
import Modal from './Modal';

export class ScrollableArea extends React.Component {

    render() {
        const scrollableArea = {
            background: 'rgba(44,42,41)',
        }

        const scAreaHeading = {
            color: '#fff',
            fontSize: '3.5vw',
            fontWeight: '100',
            letterSpacing: '2px',
            margin: '20px 0 0 0',
            WebkitTextStrokeWidth: '1px',
            WebkitTextStrokeColor: 'white',
            textShadow: '1px 1px rgba( 0, 0, 0, .3 )',

        }

        const scrollableAreaCont = {
            background: 'linear-gradient(0deg, rgba(195,212,223,1) 25%, rgba(137,141,149,1) 75%)',
            // background: 'rgb(119, 135, 162, .2)',
            display: 'block',
            overflowX: 'scroll',
            overflowY: 'none',
            minHeight: '250px',
            overflow: 'auto',
            padding: '15px 0 15px 0',
            margin: '5px 3vw 40px 3vw',
            border: '1px solid white',
            borderRadius: '3px',

        }

        const scrollableAreaContFlex = {
            display: 'flex',
            justifyContent: 'space-between',
            height: 'inherit',
        }

        var dataArr = [];

        Object.keys(this.props.jsonData).forEach(key => dataArr.push(this.props.jsonData[key]));

        return (
            <>
            <div style={ scrollableArea }>
                <h1 style={ scAreaHeading }>{this.props.heading}</h1>
                <div style={ scrollableAreaCont }>
                    <div style={ scrollableAreaContFlex }>
                        {
                            dataArr.map(item => 
                                <Modal 
                                    key={ item.id }
                                    image={ item.image } 
                                    heading={ item.heading } 
                                    text={ item.text } 
                                    buttonTxt={ item.buttonTxt } 
                                    link={ item.link }
                                />
                            )
                        }
                    </div>
                </div>
            </div>
            </>
        )
    }
}

export default ScrollableArea;
