export default function deepEqual(object1: any = [], object2: any = []) {
  const keys1 = Object.keys(object1);
  const keys2 = Object.keys(object2);

  if (keys1.length !== keys2.length) {
    return false;
  }

  for (const key of keys1) {
    const val1 = object1[key];
    const val2 = object2[key];

    if (
      (isObject(val1) || isArray(val1)) && (isObject(val2) || isArray(val2))
        ? !deepEqual(val1, val2)
        : val1 !== val2
    ) {
      return false;
    }
  }

  return true;
}
export const isMatchFieldArrayName = (name: string, searchName: string) =>
  RegExp(
    `^${searchName}[\\d+]`.replace(/\[/g, '\\[').replace(/\]/g, '\\]'),
  ).test(name);

export default (names: Set<string>, name: string) =>
