export default function onDomRemove(
  element: Ref,
  onDetachCallback: () => void,
): MutationObserver {
  const observer = new MutationObserver((): void => {
    if (isDetached(element)) {
      observer.disconnect();
      onDetachCallback();
    }
  });

  observer.observe(window.document, {
    childList: true,
    subtree: true,
  });

  return observer;
}
