import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const navCont = {
    background: 'linear-gradient(180deg, rgba(0,0,0,1) 11%, rgba(33,33,33,1) 81%)',
    width: '100%',
    zIndex: '1000',
}

const nav = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    height: '40px', 
}

const navLogoCont = {
    background: 'red',
    height: 'inherit', 
    display: 'flex', 
    justifyContent: 'center',
    alignItems: 'center',
    padding: '0 20px',
}

const navLogo = {
    height: '30px',
}

const hbIconCont = {
    backgroundColor: 'green',
    height: 'inherit',
    display: 'flex', 
    justifyContent: 'center',
    alignItems: 'center',
}

const hbIcon = {
    height: 'inherit',
    padding: '0 40px',
}

const dropMenu = {
    background: 'white',
    position: 'absolute',
    top: 0,
    left: 0,
    width: '80%',
    height: '100%',
}

const navLinks = {}
const links = {}
const navLinkText = {}

// ------------
function HamburgerNav() {
    const [showMenu, setShowMenu] = useState(false);

    let menu;
    if(showMenu) {
        menu = 
            <div style={ dropMenu }>
                 <ul style={ navLinks }>
                    <Link style={links} to='/'>
                        <div style={ navLinkText } id="homeNavBtn" >HOME</div>
                    </Link>
                    <Link style={links} to='/about'>
                        <div style={ navLinkText }>ABOUT</div>
                    </Link>
                    <Link style={links} to='/services'>
                        <div style={ navLinkText }>SERVICES</div>
                    </Link>
                    <Link style={links} to='/projects'>
                        <div style={ navLinkText }>OUR PROJECTS</div>
                    </Link>
                    <Link style={links} to='/contact'>
                        <div style={ navLinkText }>CONTACT</div>
                    </Link>
                </ul>
            </div>
    }

    return (
        <header style={ navCont }>
            <nav style={ nav }>
                <Link style={ navLogoCont } to='/'>
                    <img src="/images/navLogo.png" alt="CBSI" style={ navLogo } />
                </Link>
                <div style={ hbIconCont } onClick={() => setShowMenu(!showMenu)}>
                    <img src="/images/hbIcon.png" alt="Navigation Options" style={ hbIcon } />
                </div>

            </nav>

            { menu }

        </header>
    );
}

export default HamburgerNav;
