import React from 'react';

export class ScalableInfoBlock extends React.Component {

    render() {
        var dataArr = [];
        Object.keys(this.props.jsonData).forEach(key => dataArr.push(this.props.jsonData[key]));
        var numSections = dataArr.length;

        const scalableInfoBlock = {
            background: `${this.props.bgColor}`,
            display: 'flex',
            justifyContent: 'space-around',
            margin: '3vw 4vw',
        }

        const bl = {
            background: 'red',
            width: '100%',
            margin: '1vw',
            padding: '.5vw',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
        }

        const heading = {
            fontSize: '2.5vw',
            margin: '1vw',
        }

        const text = {
            fontSize: '1.5vw',
            margin: '1vw',
            flex: 2,
        }

        const btnTxtBl = {
            display: 'flex',
            alignItems: 'center',
        }

        const button = {
            backgorund: `url(${this.props.image})`,
        }

        const buttonText = {
            margin: '0 0 0 1vw ',
        }

        return (
            <div style={ scalableInfoBlock } className="scalableInfoBlock">
                {
                    dataArr.map(item => 
                        <div style={ bl } key={item.id}>
                            <div style={ heading }>
                                {item.heading}
                                <hr/>
                            </div>
                            <div style={ text }>
                                {item.text}
                            </div>
                            <div style={ btnTxtBl }>
                                <button style={ button }></button>
                                <div style={ buttonText }>{ item.buttonTxt }</div>
                            </div>
                        </div>
                    )
                }
            </div>
        );
    }
}

export default ScalableInfoBlock;
