import React from 'react';

function Modal(props) {

    const modalContainer = {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '40vmin',
        width: '30vmin',
        backgroundColor: 'rgba(18, 32, 39, .8)',
        color: '#eff',
        margin: '10px',
        border: '1px solid black',

    }

    const imageContainer = {
        width: '100%',
    }

    const heading = {
        margin: '5px',
    }

    const text = {
        margin: '0 8px 10px 8px',
        flex: 1,
    }

    const textHeader = { 
        fontSize: '3vw',
    }

    const textBody = { 
        fontSize: '2vw',
    }

  return (
    <>    
        <div style={ modalContainer }>
            <div style={ imageContainer }>
                <img src={props.image} alt="Modal Image" width="100%" />
            </div>
            <h2  style={ heading }>
                <div style={ textHeader }>{props.heading}</div>
            </h2>
            <div  style={ text }>
                <div style={ textBody }>{props.text}</div>
            </div>
        </div>
    </>
  );
}

export default Modal;
