import React from 'react';
import Modal from './Modal';
import modalData from './data/dataFeed.json';

function Home() {

    const homeBannerImg = {
        backgroundImage: 'url("/images/home3.png")',
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        height: '30vh',
    }

    const bannerTxt = {
        color: '#fff',
        margin: '0',
        fontFamily: 'arial, san-serif',
        fontSize: '4.5vw',
        fontWeight: '400',
        textAlign: 'left',
    }

    const buttonCont = {
        display: 'inline-block',
        float: 'left',
        margin: '1vw 0 0 0',
    }

    const btn = {
        height: '3vw',
        fontSize: '1.4vw',
        border: 'none',
        color: 'white',
        background: 'radial-gradient(circle, rgba(245,246,250,.3) 0%, rgba(26,56,111,0) 100%)',
    }

    const textCont1 = {
        background: 'rgba(26,56,111,.5)',
        padding: '6vw 0 20px 6vw',
        flex: '1',

    }
    const textCont2 = {
        display: 'block',
        background: 'rgba(100,100,100,.5)',
        flex: '1',
        "&:hover": {
            background: 'rgba(100,100,100,.8)',
        }
    }

  return (
    <>    
        <div className="content">
            <div>
                <div className="banner" style={ homeBannerImg }>
                    <div style={ textCont1 }>
                        <h2 style={ bannerTxt }>
                            Restoration.
                            <br/>
                            <b style={{fontWeight: '600'}}>Repair.</b>
                            <br/>
                            Waterproofing. 
                        </h2>
                        <div style={ buttonCont }>
                            <a href="/services">
                                <button style={ btn }>View Our Services</button>
                            </a>
                        </div>
                    </div>
                    <div style={ textCont2 }></div>
                </div>
            </div>

            {
                Object.values(modalData).forEach(modal => {
                    <Modal 
                        image={ modal.image } 
                        heading={ modal.heading }
                        text={ modal.text }></Modal>
                })

            }

        </div>
    </>
  );
}

export default Home;
