import React from 'react';

const scalableInfoBlock = {
    display: 'flex',
    margin: '5vw 5vw',
    minHeight: '30vw',
}

const bl = {
    width: '100%',
    minHeight: 'inherit',
    margin: '1vw 0',
    display: 'flex',
    flexDirection: 'column',
}

const heading = {
    fontSize: '3vw',
    fontWeight: '300',
    color: 'rgb(166, 25, 46)',
    margin: '0 2vw 2vw 2vw',
    textAlign: 'left',
}

const text = {
    fontSize: '1.9vw',
    fontWeight: '300',
    color: 'rgba(44,42,41)',
    margin: '2vw 2.2vw 4vw 2.2vw',
    flex: 2,
    lineHeight: '2.5vw',
    textAlign: 'left',
}

const btnTxtBl = {
    display: 'flex',
    alignItems: 'center',
}

const button = {
    display: 'block',
    background: `url('/images/arrowIcon.png')`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    width: '3.5vw',
    height: '3.5vw',
    margin: '0 0 0 1vw',
}

const buttonText = {
    margin: '0 0 0 1vw ',
    color: 'rgba(44,42,41)',
    fontSize: '1.6vw',
    fontWeight: 600,
}

export class ScalableInfoBlock extends React.Component {

    render() {
        var dataArr = [];
        Object.keys(this.props.jsonData).forEach(key => dataArr.push(this.props.jsonData[key]));

        const bgColor = {background: `${this.props.bgColor}`,}

        return (
            <div style={ {...scalableInfoBlock, bgColor} } className="scalableInfoBlock">
                {
                    dataArr.map((item, i) => 
                        <div style={ bl } key={item.id}>
                            <div style={ heading }>
                                {item.heading}
                                <hr/>
                            </div>
                            <div style={ text }>
                                {item.text}
                            </div>
                            <a href={item.link} style={{textDecoration: 'none'}}>
                                <div style={ btnTxtBl }>
                                    <div style={ button }></div>
                                    <div style={ buttonText }>{ item.buttonTxt }</div>
                                </div>
                            </a>
                        </div>
                    )
                }
            </div>
        );
    }
}

export default ScalableInfoBlock;
