import React from 'react';

const imageQuoteBlock = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '4vw 0 4vw 0',
}

const imageQuoteBlockInner = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '80%',
}

const img = {
    height: '30vw',
    position: 'relative',
    left: '5vw',
}

const textBlock = {
    background: 'rgba(255, 255, 255, .95)',
    color: 'black',
    maxHeight: '20vw',
    padding: '2vw 3vw',
    border: '1px solid rgb(166, 25, 46)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    left: '-5vw',
    top: '3vw',
}

const text = {
    fontSize: '3vw',
}

function ImageQuoteBlock(props) {


    return (
        <div style={ imageQuoteBlock } className="imageQuoteBlock">
            <div style={ imageQuoteBlockInner }>
                <img src={ props.image || './images/imgError.jpg' } alt={props.imgDesc} style={ img } />
                <div style={ textBlock }>
                    <div style={ text }>
                        { props.text }
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ImageQuoteBlock;
