import React from 'react';

function Modal(props) {

    const modalContainer = {
        minHeight: '300px',
        minWidth: '250px',
        height: '40vw',
        maxHeight: '450px',
        backgroundColor: 'rgba(26,56,111,.65)',
        color: '#fff',
        margin: '0 20px 0 20px',
        borderRadius: '3%',
        display: 'flex',
        flexDirection: 'column',

    }

    const imageContainer = {
        width: '100%',
        height: '50%',
        background: `url(${props.image})`,
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
    }

    const contentContainer = {
        backgroundColor: 'rgba(4,4,4,.4)',
        height: '50%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-around',
        alignItems: 'center',
    }

    const heading = {
        backgroundColor: 'red',
        flex: '1',
        fontSize: '100%',
    }

    const text = {
        backgroundColor: 'green',
        flex: '3',
        fontSize: '80%',
        padding: '1vmin',
    }

    const button = {
        backgroundColor: 'cyan',
        flex: '1',
        border: 'none',
        height: '100%',
    }

  return (
    <>    
        <div style={ modalContainer } className="modalContF">
            <div style={ imageContainer }></div>
            <div style={ contentContainer }>
                <h1  style={ heading }>
                    {props.heading}
                </h1>
                <div  style={ text }>
                    {props.text}
                </div>
                <div>
                    <button style={ button }>{ props.buttonTxt || "Button" }</button>
                </div>
            </div>
        </div>
    </>
  );
}

export default Modal;
