import React from 'react';
import styled from 'styled-components'

function ContactCard(props) {

    const contactCard = {
        display: 'flex',
        justifyContent: 'space-between',
        width: '100%',
        height: '100%',
    }

    const imgCont = styled.div`
        background: url(${props.image});
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        display: 'block',
        width: '19vh',
        borderRadius: '50%',
        border: '2px solid rgb(166, 25, 46)',
        &:onHover {
            borderRadius: '5px solid green'.
        },
    }
    `
    const contDesc = {
        height: '100%',
        width: '60%',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
    }

    const nameDesc = {
        display: 'flex',
        flexDirection: 'column',
        margin: '10px 0 0 0'
    }

    const contactDesc = {

        fontSize: '3vw',
        fontWeight: '600',
        letterSpacing: '1px',
        margin: '4vh 0 5vh 0',
    }


    return (
        <>
            <div style={ contactCard }>
                <div style={ imgCont }></div>
                <div style={ contDesc }>
                    <div style={ nameDesc }>
                        <div style={{fontWeight: '600',fontSize: "2.5vw"}}> {props.name} </div>
                        <div style={{fontSize: '10px'}}> {props.title} </div>
                    </div>
                    <div style={contactDesc}>
                        {props.email}
                    </div>
                </div>
            </div>
        </>
    );
}

export default ContactCard;
