import React from 'react';
import { Blockquote } from 'glamorous';

export class ScalableInfoBlock extends React.Component {

    render() {
        var dataArr = [];
        Object.keys(this.props.jsonData).forEach(key => dataArr.push(this.props.jsonData[key]));
        var numSections = dataArr.length;

        const scalableInfoBlock = {
            background: `${this.props.bgColor}`,
            display: 'flex',
            margin: '5vw 5vw',
            minHeight: '30vw',
        }

        const bl = {
            // backgroundColor: 'cyan',
            width: '100%',
            minHeight: 'inherit',
            margin: '1vw 0',
            display: 'flex',
            flexDirection: 'column',
            // borderRight: '1px solid black',
            // borderLeft: '1px solid black',
        }

        const heading = {
            fontSize: '2.5vw',
            fontWeight: '600',
            color: 'rgb(166, 25, 46)',
            margin: '0 2vw 2vw 2vw',
            textAlign: 'left',
        }

        const text = {
            fontSize: '1.9vw',
            margin: '2vw 2.2vw 4vw 2.2vw',
            flex: 2,
            lineHeight: '2.5vw',
            textAlign: 'left',
        }

        const btnTxtBl = {
            display: 'flex',
            alignItems: 'center',
            textDecoration: 'none',
        }

        const button = {
            display: 'block',
            background: `url('/images/arrowIcon.png')`,
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
            width: '3.5vw',
            height: '3.5vw',
            margin: '0 0 0 1vw',
        }

        const buttonText = {
            margin: '0 0 0 1vw ',
            fontSize: '1.6vw',
            fontWeight: 600,
        }

        return (
            <div style={ scalableInfoBlock } className="scalableInfoBlock">
                {
                    dataArr.map((item, i) => 
                    <div style={ bl } key={item.id}>
                    <div style={ heading }>
                        {item.heading}
                        <hr/>
                    </div>
                    <div style={ text }>
                        {item.text}
                    </div>
                    <a href={item.link}>
                        <div style={ btnTxtBl }>
                            <div style={ button }></div>
                            <div style={ buttonText }>{ item.buttonTxt }</div>
                        </div>
                    </a>
                </div>
                    )
                }
            </div>
        );
    }
}

export default ScalableInfoBlock;
