import React from 'react';

const imageQuoteBlock = {
    padding: '4vw 0 4vw 0',
}

const imageQuoteBlockOuter = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
}

const imageQuoteBlockInner = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '80%',
}

const img = {
    height: '25vw',
    position: 'relative',
    left: '5vw',
    // borderRadius: '10px',
    border: '1px solid black',
}

const textBlock = {
    background: 'white',
    color: 'black',
    maxHeight: '11vw',
    padding: '3vw 3vw',
    border: '3px solid rgb(166, 25, 46)',
    borderRadius: '10px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    left: '-5vw',
    top: '2vw',
}

const text = {
    fontFamily: 'Open Sans, sans-serif',
    fontSize: '2.5vw',
    fontWeight: '00',
    lineHeight: '3.5vw',
}

const authorBlock = {
    zIndex: 1,
    position: 'relative',
    top: '5vw',
    left: '14vw',
    display: 'inline-block',
}

const authorText = {
    fontSize: '2.3vw',
    fontWeight: 'bold',
}

function ImageQuoteBlock(props) {


    return (
        <div style={ imageQuoteBlock } className="imageQuoteBlock">
            <div style={ authorBlock }>
                <div style={ authorText }>
                    - { props.author }
                </div>
            </div>
            <div style={ imageQuoteBlockOuter }>
                <div style={ imageQuoteBlockInner }>
                    <img src={ props.image || './images/imgError.jpg' } alt={props.imgDesc} style={ img } />
                    <div style={ textBlock }>
                        <div style={ text }>
                            "{ props.text }"
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ImageQuoteBlock;
