import React from 'react';
import ScrollableArea from './components/ScrollableArea';
import ContactBlock from './components/ContactBlock';
import Footer from './components/Footer';
import ModalData from './data/homeModalsData.json';
import InfoData from './data/homeInfoBlockData.json';
import ScalableInfoBlock from './components/ScalableInfoBlock';

function Home() {

    const homeBannerImg = {
        backgroundImage: 'url("/images/home3.png")',
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        height: '30vh',
    }

    const bannerTxt = {
        color: '#fff',
        margin: '0',
        fontFamily: 'arial, san-serif',
        fontSize: '4.5vw',
        fontWeight: '100',
        textAlign: 'left',
    }

    const buttonCont = {
        display: 'inline-block',
        float: 'left',
        margin: '1vw 0 0 0',
    }

    const btn = {
        height: '3vw',
        fontSize: '1.4vw',
        border: 'none',
        color: 'white',
        background: 'radial-gradient(circle, rgb(166, 25, 46) 0%, rgba(26,56,111,0) 100%)',
    }

    const textCont1 = {
        background: 'rgba(26,56,111,.5)',
        // background: 'rgba(232,54,44,.3)',
        padding: '6vw 0 20px 6vw',
        flex: '1',

    }
    const textCont2 = {
        display: 'block',
        background: 'rgba(100,100,100,.5)',
        flex: '1',
    }

  return (
    <>    
        <div className="content">
            <div className="banner" style={ homeBannerImg }>
                <div style={ textCont1 }>
                    <h2 style={ bannerTxt }>
                        Restoration.
                        <br/>
                        <b style={{fontWeight: '600'}}>Repair.</b>
                        <br/>
                        Waterproofing. 
                    </h2>
                    <div style={ buttonCont }>
                        <a href="/services">
                            <button style={ btn }>View All Services</button>
                        </a>
                    </div>
                </div>
                <div style={ textCont2 }></div>
            </div>

            <ScalableInfoBlock sections={3}/>

            <ScrollableArea heading="Recent Projects" jsonData={ModalData}/>

            <ContactBlock/>

            <Footer />

        </div>
    </>
  );
}

export default Home;
